## 1. 扣子平台多轮对话逻辑实现

首先，我们需要创建一个语音对话管理器来处理多轮对话：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/voice/VoiceDialogManager.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.voice

import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import top.minepixel.rdk.data.model.CommandAction
import top.minepixel.rdk.data.model.RobotCommand
import javax.inject.Inject
import javax.inject.Singleton

/**
 * 语音对话管理器 - 处理多轮对话逻辑
 */
@Singleton
class VoiceDialogManager @Inject constructor() {

 // 对话状态
 private val _dialogState = MutableStateFlow<DialogState>(DialogState.Idle)
 val dialogState: StateFlow<DialogState> = _dialogState.asStateFlow()

 // 当前对话上下文
 private var currentContext: DialogContext? = null

 // 处理语音输入
 fun processVoiceInput(text: String): ProcessResult {
 // 检查是否在多轮对话中
 if (_dialogState.value is DialogState.InConversation) {
 return continueConversation(text)
 }

 // 尝试匹配基础指令
 val basicCommand = matchBasicCommand(text)
 if (basicCommand != null) {
 return ProcessResult.Command(basicCommand)
 }

 // 尝试匹配需要多轮对话的指令
 val dialogIntent = matchDialogIntent(text)
 if (dialogIntent != null) {
 startConversation(dialogIntent)
 return ProcessResult.Response(dialogIntent.initialResponse)
 }

 // 无法识别的指令
 return ProcessResult.Response("抱歉，我不明白您的指令。您可以尝试说"开始清洁"、"停止清洁"或"回到基站"等指令。")
 }

 // 匹配基础指令
 private fun matchBasicCommand(text: String): RobotCommand? {
 return when {
 text.contains("开始清洁") || text.contains("开始打扫") -> 
 RobotCommand(CommandAction.START_CLEANING)

 text.contains("停止清洁") || text.contains("停止打扫") -> 
 RobotCommand(CommandAction.STOP_CLEANING)

 text.contains("回到基站") || text.contains("回充") || text.contains("充电") -> 
 RobotCommand(CommandAction.RETURN_TO_DOCK)

 text.contains("找机器人") || text.contains("在哪里") -> 
 RobotCommand(CommandAction.LOCATE)

 else -> null
 }
 }

 // 匹配需要多轮对话的意图
 private fun matchDialogIntent(text: String): DialogIntent? {
 return when {
 text.contains("定点清洁") || text.contains("清洁特定区域") -> {
 DialogIntent(
 type = DialogType.SPOT_CLEANING,
 initialResponse = "您想要清洁哪个区域？例如：客厅、卧室、厨房",
 steps = listOf(DialogStep.AREA_SELECTION)
 )
 }

 text.contains("设置清洁模式") || text.contains("更改清洁模式") -> {
 DialogIntent(
 type = DialogType.SET_CLEANING_MODE,
 initialResponse = "您想设置什么清洁模式？标准、强力还是安静模式？",
 steps = listOf(DialogStep.MODE_SELECTION)
 )
 }

 text.contains("创建定时任务") || text.contains("设置定时清洁") -> {
 DialogIntent(
 type = DialogType.SCHEDULE_TASK,
 initialResponse = "您想在什么时间开始清洁？",
 steps = listOf(DialogStep.TIME_SELECTION, DialogStep.AREA_SELECTION, DialogStep.CONFIRMATION)
 )
 }

 else -> null
 }
 }

 // 开始多轮对话
 private fun startConversation(intent: DialogIntent) {
 currentContext = DialogContext(
 intent = intent,
 currentStep = 0,
 collectedData = mutableMapOf()
 )
 _dialogState.value = DialogState.InConversation(
 prompt = intent.initialResponse,
 step = intent.steps[0]
 )
 }

 // 继续多轮对话
 private fun continueConversation(text: String): ProcessResult {
 val context = currentContext ?: return ProcessResult.Response("对话已结束，请重新开始")
 val intent = context.intent
 val currentStep = intent.steps[context.currentStep]

 // 处理当前步骤的输入
 when (currentStep) {
 DialogStep.AREA_SELECTION -> {
 context.collectedData["area"] = text
 }
 DialogStep.MODE_SELECTION -> {
 context.collectedData["mode"] = text
 }
 DialogStep.TIME_SELECTION -> {
 context.collectedData["time"] = text
 }
 DialogStep.CONFIRMATION -> {
 if (text.contains("是") || text.contains("确认") || text.contains("好的")) {
 context.collectedData["confirmed"] = "true"
 } else {
 // 用户取消
 resetConversation()
 return ProcessResult.Response("已取消操作")
 }
 }
 }

 // 移动到下一步
 context.currentStep++

 // 检查是否完成所有步骤
 if (context.currentStep >= intent.steps.size) {
 // 对话完成，生成命令
 val command = generateCommandFromContext(context)
 resetConversation()
 return if (command != null) {
 ProcessResult.Command(command)
 } else {
 ProcessResult.Response("操作已完成")
 }
 }

 // 继续下一步对话
 val nextStep = intent.steps[context.currentStep]
 val nextPrompt = getPromptForStep(nextStep, context)
 _dialogState.value = DialogState.InConversation(prompt = nextPrompt, step = nextStep)
 return ProcessResult.Response(nextPrompt)
 }

 // 根据步骤生成提示语
 private fun getPromptForStep(step: DialogStep, context: DialogContext): String {
 return when (step) {
 DialogStep.AREA_SELECTION -> "您想要清洁哪个区域？"
 DialogStep.MODE_SELECTION -> "您想使用什么清洁模式？标准、强力还是安静模式？"
 DialogStep.TIME_SELECTION -> "您想在什么时间开始清洁？"
 DialogStep.CONFIRMATION -> {
 val area = context.collectedData["area"] ?: "指定区域"
 val time = context.collectedData["time"]

 if (time != null) {
 "确认在$time 清洁$area 吗？"
 } else {
 "确认清洁$area 吗？"
 }
 }
 }
 }

 // 从对话上下文生成命令
 private fun generateCommandFromContext(context: DialogContext): RobotCommand? {
 return when (context.intent.type) {
 DialogType.SPOT_CLEANING -> {
 val area = context.collectedData["area"] ?: return null
 RobotCommand(
 action = CommandAction.CLEAN_SPOT,
 params = mapOf("area" to area)
 )
 }
 DialogType.SET_CLEANING_MODE -> {
 val mode = context.collectedData["mode"] ?: return null
 val cleaningMode = when {
 mode.contains("标准") -> "STANDARD"
 mode.contains("强力") -> "STRONG"
 mode.contains("安静") -> "QUIET"
 else -> "STANDARD"
 }
 RobotCommand(
 action = CommandAction.SET_MODE,
 params = mapOf("mode" to cleaningMode)
 )
 }
 DialogType.SCHEDULE_TASK -> {
 val time = context.collectedData["time"] ?: return null
 val area = context.collectedData["area"] ?: return null
 RobotCommand(
 action = CommandAction.START_CLEANING,
 params = mapOf(
 "scheduled" to "true",
 "time" to time,
 "area" to area
 )
 )
 }
 }
 }

 // 重置对话状态
 fun resetConversation() {
 currentContext = null
 _dialogState.value = DialogState.Idle
 }

 // 对话状态
 sealed class DialogState {
 object Idle : DialogState()
 data class InConversation(val prompt: String, val step: DialogStep) : DialogState()
 }

 // 对话意图
 data class DialogIntent(
 val type: DialogType,
 val initialResponse: String,
 val steps: List<DialogStep>
 )

 // 对话上下文
 data class DialogContext(
 val intent: DialogIntent,
 var currentStep: Int,
 val collectedData: MutableMap<String, String>
 )

 // 处理结果
 sealed class ProcessResult {
 data class Command(val command: RobotCommand) : ProcessResult()
 data class Response(val text: String) : ProcessResult()
 }

 // 对话类型
 enum class DialogType {
 SPOT_CLEANING,
 SET_CLEANING_MODE,
 SCHEDULE_TASK
 }

 // 对话步骤
 enum class DialogStep {
 AREA_SELECTION,
 MODE_SELECTION,
 TIME_SELECTION,
 CONFIRMATION
 }
}
```
</augment_code_snippet>

## 2. 语音识别服务实现

接下来，我们需要创建一个语音识别服务，连接到扣子平台：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/voice/VoiceRecognitionService.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.voice

import android.content.Context
import android.util.Log
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import top.minepixel.rdk.data.model.RobotCommand
import top.minepixel.rdk.data.repository.RobotRepository
import java.io.BufferedReader
import java.io.InputStreamReader
import javax.inject.Inject
import javax.inject.Singleton

private const val TAG = "VoiceRecognition"

/**
 * 语音识别服务 - 连接扣子平台
 */
@Singleton
class VoiceRecognitionService @Inject constructor(
 private val context: Context,
 private val robotRepository: RobotRepository,
 private val dialogManager: VoiceDialogManager
) {
 private val scope = CoroutineScope(Dispatchers.IO)

 // 语音识别状态
 private val _recognitionState = MutableStateFlow<RecognitionState>(RecognitionState.Idle)
 val recognitionState: StateFlow<RecognitionState> = _recognitionState.asStateFlow()

 // 最后识别的文本
 private val _lastRecognizedText = MutableStateFlow<String>("")
 val lastRecognizedText: StateFlow<String> = _lastRecognizedText.asStateFlow()

 // 最后的响应文本
 private val _lastResponseText = MutableStateFlow<String>("")
 val lastResponseText: StateFlow<String> = _lastResponseText.asStateFlow()

 // 启动语音识别
 fun startListening() {
 if (_recognitionState.value is RecognitionState.Listening) {
 return
 }

 _recognitionState.value = RecognitionState.Listening

 // 在实际应用中，这里应该调用扣子平台的语音识别API
 // 这里我们使用模拟数据进行演示
 simulateVoiceRecognition()
 }

 // 停止语音识别
 fun stopListening() {
 if (_recognitionState.value !is RecognitionState.Listening) {
 return
 }

 _recognitionState.value = RecognitionState.Processing
 // 实际应用中，这里应该停止扣子平台的语音识别
 }

 // 模拟语音识别过程
 private fun simulateVoiceRecognition() {
 scope.launch {
 try {
 // 在实际应用中，这里应该连接到扣子平台的Docker容器
 // 使用RDK X5开发板上的语音识别功能

 // 模拟调用扣子平台的语音识别
 // 实际应用中，应该使用Docker命令调用扣子平台的语音识别服务
 val recognizedText = simulateKouziPlatformRecognition()

 _lastRecognizedText.value = recognizedText
 _recognitionState.value = RecognitionState.Processing

 // 处理识别结果
 processRecognizedText(recognizedText)

 } catch (e: Exception) {
 Log.e(TAG, "语音识别失败", e)
 _recognitionState.value = RecognitionState.Error("语音识别失败: ${e.message}")
 }
 }
 }

 // 模拟扣子平台的语音识别
 private fun simulateKouziPlatformRecognition(): String {
 // 在实际应用中，这里应该执行Docker命令调用扣子平台的语音识别服务
 // 例如：
 // val process = Runtime.getRuntime().exec(
 // "docker exec -it xfspeech_container python /path/to/recognition_script.py"
 // )
 // val reader = BufferedReader(InputStreamReader(process.inputStream))
 // return reader.readLine() ?: ""

 // 这里返回模拟数据
 return "开始清洁"
 }

 // 处理识别到的文本
 private fun processRecognizedText(text: String) {
 scope.launch {
 val result = dialogManager.processVoiceInput(text)

 when (result) {
 is VoiceDialogManager.ProcessResult.Command -> {
 // 执行命令
 executeCommand(result.command)
 _lastResponseText.value = getResponseForCommand(result.command)
 }
 is VoiceDialogManager.ProcessResult.Response -> {
 // 显示响应
 _lastResponseText.value = result.text
 }
 }

 _recognitionState.value = RecognitionState.Idle
 }
 }

 // 执行机器人命令
 private suspend fun executeCommand(command: RobotCommand) {
 robotRepository.sendCommand(command)
 .onSuccess {
 Log.d(TAG, "命令执行成功: ${command.action}")
 }
 .onFailure { e ->
 Log.e(TAG, "命令执行失败: ${command.action}", e)
 }
 }

 // 根据命令生成响应文本
 private fun getResponseForCommand(command: RobotCommand): String {
 return when (command.action) {
 CommandAction.START_CLEANING -> "好的，开始清洁"
 CommandAction.STOP_CLEANING -> "已停止清洁"
 CommandAction.RETURN_TO_DOCK -> "正在返回充电座"
 CommandAction.LOCATE -> "已发出提示音，帮助您找到机器人"
 CommandAction.CLEAN_SPOT -> {
 val area = command.params["area"] ?: "指定区域"
 "开始清洁${area}"
 }
 CommandAction.SET_MODE -> {
 val mode = command.params["mode"] ?: "标准"
 "已设置为${mode}模式"
 }
 else -> "命令已执行"
 }
 }

 // 语音识别状态
 sealed class RecognitionState {
 object Idle : RecognitionState()
 object Listening : RecognitionState()
 object Processing : RecognitionState()
 data class Error(val message: String) : RecognitionState()
 }
}
```
</augment_code_snippet>

## 3. 更新VoiceControlViewModel

现在，我们需要更新VoiceControlViewModel来使用我们的语音对话管理器和语音识别服务：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/ui/viewmodel/VoiceControlViewModel.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.ui.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch
import top.minepixel.rdk.data.model.CommandAction
import top.minepixel.rdk.data.model.RobotCommand
import top.minepixel.rdk.data.repository.RobotRepository
import top.minepixel.rdk.voice.VoiceDialogManager
import top.minepixel.rdk.voice.VoiceRecognitionService
import javax.inject.Inject

private const val TAG = "VoiceControlViewModel"

@HiltViewModel
class VoiceControlViewModel @Inject constructor(
 private val robotRepository: RobotRepository,
 private val voiceRecognitionService: VoiceRecognitionService,
 private val dialogManager: VoiceDialogManager
) : ViewModel() {

 // UI状态
 private val _uiState = MutableStateFlow(VoiceControlUiState())
 val uiState: StateFlow<VoiceControlUiState> = _uiState.asStateFlow()

 // 初始化
 init {
 // 监听语音识别状态
 viewModelScope.launch {
 voiceRecognitionService.recognitionState.collectLatest { state ->
 _uiState.value = _uiState.value.copy(
 isListening = state is VoiceRecognitionService.RecognitionState.Listening,
 isProcessing = state is VoiceRecognitionService.RecognitionState.Processing,
 errorMessage = if (state is VoiceRecognitionService.RecognitionState.Error) state.message else null
 )
 }
 }

 // 监听识别文本
 viewModelScope.launch {
 voiceRecognitionService.lastRecognizedText.collectLatest { text ->
 if (text.isNotEmpty()) {
 _uiState.value = _uiState.value.copy(recognizedText = text)
 }
 }
 }

 // 监听响应文本
 viewModelScope.launch {
 voiceRecognitionService.lastResponseText.collectLatest { text ->
 if (text.isNotEmpty()) {
 _uiState.value = _uiState.value.copy(responseText = text)
 }
 }
 }

 // 监听对话状态
 viewModelScope.launch {
 dialogManager.dialogState.collectLatest { state ->
 _uiState.value = _uiState.value.copy(
 isInConversation = state is VoiceDialogManager.DialogState.InConversation,
 conversationPrompt = if (state is VoiceDialogManager.DialogState.InConversation) state.prompt else null
 )
 }
 }
 }

 // 开始语音识别
 fun startListening() {
 voiceRecognitionService.startListening()
 }

 // 停止语音识别
 fun stopListening() {
 voiceRecognitionService.stopListening()
 }

 // 发送文本命令（用于测试）
 fun sendTextCommand(text: String) {
 viewModelScope.launch {
 _uiState.value = _uiState.value.copy(recognizedText = text)
 val result = dialogManager.processVoiceInput(text)

 when (result) {
 is VoiceDialogManager.ProcessResult.Command -> {
 robotRepository.sendCommand(result.command)
 _uiState.value = _uiState.value.copy(
 responseText = getResponseForCommand(result.command)
 )
 }
 is VoiceDialogManager.ProcessResult.Response -> {
 _uiState.value = _uiState.value.copy(
 responseText = result.text
 )
 }
 }
 }
 }

 // 重置对话
 fun resetConversation() {
 dialogManager.resetConversation()
 _uiState.value = _uiState.value.copy(
 recognizedText = "",
 responseText = "",
 conversationPrompt = null,
 isInConversation = false
 )
 }

 // 根据命令生成响应文本
 private fun getResponseForCommand(command: RobotCommand): String {
 return when (command.action) {
 CommandAction.START_CLEANING -> "好的，开始清洁"
 CommandAction.STOP_CLEANING -> "已停止清洁"
 CommandAction.RETURN_TO_DOCK -> "正在返回充电座"
 CommandAction.LOCATE -> "已发出提示音，帮助您找到机器人"
 CommandAction.CLEAN_SPOT -> {
 val area = command.params["area"] ?: "指定区域"
 "开始清洁${area}"
 }
 CommandAction.SET_MODE -> {
 val mode = command.params["mode"] ?: "标准"
 "已设置为${mode}模式"
 }
 else -> "命令已执行"
 }
 }
}

// 语音控制UI状态
data class VoiceControlUiState(
 val isListening: Boolean = false,
 val isProcessing: Boolean = false,
 val recognizedText: String = "",
 val responseText: String = "",
 val errorMessage: String? = null,
 val isInConversation: Boolean = false,
 val conversationPrompt: String? = null
)
```
</augment_code_snippet>

## 4. 更新VoiceControlScreen

现在，我们需要更新VoiceControlScreen来使用我们的ViewModel：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/ui/screens/VoiceControlScreen.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.ui.screens

import androidx.compose.animation.AnimatedVisibility
import androidx.compose.animation.core.Spring
import androidx.compose.animation.core.animateFloatAsState
import androidx.compose.animation.core.spring
import androidx.compose.animation.fadeIn
import androidx.compose.animation.fadeOut
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.*
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.draw.scale
import androidx.compose.ui.graphics.Brush
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
