## 1. 连接扣子平台

首先，我们需要创建一个服务来连接扣子平台的Docker容器。根据提供的资料，扣子平台是通过Docker容器运行的，我们可以通过以下方式连接：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/voice/KouziPlatformService.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.voice

import android.content.Context
import android.util.Log
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import java.io.BufferedReader
import java.io.InputStreamReader
import java.io.OutputStreamWriter
import java.net.Socket
import javax.inject.Inject
import javax.inject.Singleton

private const val TAG = "KouziPlatform"

/**
 * 扣子平台服务 - 负责与扣子平台Docker容器通信
 */
@Singleton
class KouziPlatformService @Inject constructor(
 private val context: Context
) {
 private val scope = CoroutineScope(Dispatchers.IO)

 // 连接状态
 private val _connectionState = MutableStateFlow<ConnectionState>(ConnectionState.Disconnected)
 val connectionState: StateFlow<ConnectionState> = _connectionState.asStateFlow()

 // 语音识别结果
 private val _recognitionResult = MutableStateFlow<RecognitionResult?>(null)
 val recognitionResult: StateFlow<RecognitionResult?> = _recognitionResult.asStateFlow()

 // 扣子平台Docker容器的IP和端口
 private val KOUZI_DOCKER_HOST = "localhost" // 如果在同一设备上运行，使用localhost
 private val KOUZI_DOCKER_PORT = 8080 // 根据实际配置调整

 private var socket: Socket? = null
 private var isConnected = false

 /**
 * 连接到扣子平台Docker容器
 */
 fun connect() {
 if (isConnected) return

 _connectionState.value = ConnectionState.Connecting

 scope.launch {
 try {
 // 尝试通过Socket连接到扣子平台Docker容器
 // 注意：在实际应用中，可能需要使用adb命令或其他方式与Docker通信
 socket = Socket(KOUZI_DOCKER_HOST, KOUZI_DOCKER_PORT)
 isConnected = true
 _connectionState.value = ConnectionState.Connected

 // 启动监听线程
 startListening()

 Log.d(TAG, "成功连接到扣子平台")
 } catch (e: Exception) {
 Log.e(TAG, "连接扣子平台失败", e)
 _connectionState.value = ConnectionState.Error("连接失败: ${e.message}")

 // 尝试使用Docker命令连接
 connectViaDocker()
 }
 }
 }

 /**
 * 通过Docker命令连接扣子平台
 */
 private fun connectViaDocker() {
 scope.launch {
 try {
 // 使用Runtime执行Docker命令
 // 这里使用了RDK X5资料中提供的Docker脚本
 val process = Runtime.getRuntime().exec(
 "su -c \"cd /data/local/tmp && ./docker_xfspeech_ros1.sh\""
 )

 // 读取命令输出
 val reader = BufferedReader(InputStreamReader(process.inputStream))
 var line: String?
 val output = StringBuilder()

 while (reader.readLine().also { line = it } != null) {
 output.append(line).append("\n")
 }

 // 等待命令执行完成
 val exitCode = process.waitFor()

 if (exitCode == 0) {
 Log.d(TAG, "Docker容器启动成功: $output")
 _connectionState.value = ConnectionState.Connected
 isConnected = true
 } else {
 Log.e(TAG, "Docker容器启动失败: $output")
 _connectionState.value = ConnectionState.Error("Docker启动失败，退出码: $exitCode")
 }
 } catch (e: Exception) {
 Log.e(TAG, "执行Docker命令失败", e)
 _connectionState.value = ConnectionState.Error("Docker命令执行失败: ${e.message}")
 }
 }
 }

 /**
 * 断开与扣子平台的连接
 */
 fun disconnect() {
 if (!isConnected) return

 scope.launch {
 try {
 socket?.close()

 // 如果使用Docker，可能需要停止容器
 val process = Runtime.getRuntime().exec(
 "su -c \"docker stop xfspeech_container\""
 )
 process.waitFor()

 isConnected = false
 _connectionState.value = ConnectionState.Disconnected
 Log.d(TAG, "已断开与扣子平台的连接")
 } catch (e: Exception) {
 Log.e(TAG, "断开连接失败", e)
 _connectionState.value = ConnectionState.Error("断开连接失败: ${e.message}")
 }
 }
 }

 /**
 * 启动语音识别
 */
 fun startVoiceRecognition() {
 if (!isConnected) {
 _connectionState.value = ConnectionState.Error("未连接到扣子平台")
 return
 }

 scope.launch {
 try {
 // 发送启动语音识别的命令到扣子平台
 val command = "{\"action\":\"start_recognition\"}"
 sendCommand(command)

 Log.d(TAG, "已发送语音识别启动命令")
 } catch (e: Exception) {
 Log.e(TAG, "启动语音识别失败", e)
 _connectionState.value = ConnectionState.Error("启动语音识别失败: ${e.message}")
 }
 }
 }

 /**
 * 停止语音识别
 */
 fun stopVoiceRecognition() {
 if (!isConnected) return

 scope.launch {
 try {
 // 发送停止语音识别的命令到扣子平台
 val command = "{\"action\":\"stop_recognition\"}"
 sendCommand(command)

 Log.d(TAG, "已发送语音识别停止命令")
 } catch (e: Exception) {
 Log.e(TAG, "停止语音识别失败", e)
 }
 }
 }

 /**
 * 发送语音合成请求
 */
 fun speakText(text: String) {
 if (!isConnected) {
 _connectionState.value = ConnectionState.Error("未连接到扣子平台")
 return
 }

 scope.launch {
 try {
 // 发送语音合成命令到扣子平台
 val command = "{\"action\":\"speak\",\"text\":\"$text\"}"
 sendCommand(command)

 Log.d(TAG, "已发送语音合成命令: $text")
 } catch (e: Exception) {
 Log.e(TAG, "语音合成请求失败", e)
 _connectionState.value = ConnectionState.Error("语音合成失败: ${e.message}")
 }
 }
 }

 /**
 * 发送命令到扣子平台
 */
 private fun sendCommand(command: String) {
 socket?.let { socket ->
 val writer = OutputStreamWriter(socket.getOutputStream())
 writer.write(command + "\n")
 writer.flush()
 } ?: run {
 // 如果Socket不可用，尝试通过Docker命令发送
 sendCommandViaDocker(command)
 }
 }

 /**
 * 通过Docker命令发送指令到扣子平台
 */
 private fun sendCommandViaDocker(command: String) {
 scope.launch {
 try {
 // 使用Docker exec命令向容器发送指令
 val dockerCmd = "su -c \"docker exec xfspeech_container python /app/command_handler.py '$command'\""
 val process = Runtime.getRuntime().exec(dockerCmd)

 // 读取命令输出
 val reader = BufferedReader(InputStreamReader(process.inputStream))
 var line: String?
 val output = StringBuilder()

 while (reader.readLine().also { line = it } != null) {
 output.append(line).append("\n")
 }

 // 等待命令执行完成
 val exitCode = process.waitFor()

 if (exitCode == 0) {
 Log.d(TAG, "Docker命令执行成功: $output")
 // 解析输出结果
 parseDockerOutput(output.toString())
 } else {
 Log.e(TAG, "Docker命令执行失败: $output")
 }
 } catch (e: Exception) {
 Log.e(TAG, "执行Docker命令失败", e)
 }
 }
 }

 /**
 * 解析Docker命令输出
 */
 private fun parseDockerOutput(output: String) {
 // 根据实际输出格式解析结果
 if (output.contains("recognition_result")) {
 val text = output.substringAfter("recognition_result:").trim()
 _recognitionResult.value = RecognitionResult(text)
 }
 }

 /**
 * 启动监听线程，接收扣子平台的响应
 */
 private fun startListening() {
 scope.launch {
 try {
 socket?.let { socket ->
 val reader = BufferedReader(InputStreamReader(socket.getInputStream()))
 var line: String?

 while (reader.readLine().also { line = it } != null) {
 line?.let { processResponse(it) }
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "监听扣子平台响应失败", e)
 _connectionState.value = ConnectionState.Error("监听失败: ${e.message}")
 }
 }
 }

 /**
 * 处理扣子平台的响应
 */
 private fun processResponse(response: String) {
 try {
 Log.d(TAG, "收到扣子平台响应: $response")

 // 解析JSON响应
 if (response.contains("recognition_result")) {
 val text = response.substringAfter("recognition_result\":\"").substringBefore("\"")
 _recognitionResult.value = RecognitionResult(text)
 }
 } catch (e: Exception) {
 Log.e(TAG, "处理响应失败", e)
 }
 }

 // 连接状态
 sealed class ConnectionState {
 object Disconnected : ConnectionState()
 object Connecting : ConnectionState()
 object Connected : ConnectionState()
 data class Error(val message: String) : ConnectionState()
 }

 // 识别结果
 data class RecognitionResult(val text: String)
}
```
</augment_code_snippet>

## 2. 更新VoiceRecognitionService以使用扣子平台

现在，我们需要更新VoiceRecognitionService，使其使用KouziPlatformService：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/voice/VoiceRecognitionService.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.voice

import android.content.Context
import android.util.Log
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.flow.collectLatest
import kotlinx.coroutines.launch
import top.minepixel.rdk.data.model.RobotCommand
import top.minepixel.rdk.data.repository.RobotRepository
import javax.inject.Inject
import javax.inject.Singleton

private const val TAG = "VoiceRecognition"

/**
 * 语音识别服务 - 连接扣子平台
 */
@Singleton
class VoiceRecognitionService @Inject constructor(
 private val context: Context,
 private val robotRepository: RobotRepository,
 private val dialogManager: VoiceDialogManager,
 private val kouziPlatformService: KouziPlatformService
) {
 private val scope = CoroutineScope(Dispatchers.IO)

 // 语音识别状态
 private val _recognitionState = MutableStateFlow<RecognitionState>(RecognitionState.Idle)
 val recognitionState: StateFlow<RecognitionState> = _recognitionState.asStateFlow()

 // 最后识别的文本
 private val _lastRecognizedText = MutableStateFlow<String>("")
 val lastRecognizedText: StateFlow<String> = _lastRecognizedText.asStateFlow()

 // 最后的响应文本
 private val _lastResponseText = MutableStateFlow<String>("")
 val lastResponseText: StateFlow<String> = _lastResponseText.asStateFlow()

 init {
 // 监听扣子平台的识别结果
 scope.launch {
 kouziPlatformService.recognitionResult.collectLatest { result ->
 result?.let {
 _lastRecognizedText.value = it.text
 _recognitionState.value = RecognitionState.Processing

 // 处理识别结果
 processRecognizedText(it.text)
 }
 }
 }

 // 监听扣子平台的连接状态
 scope.launch {
 kouziPlatformService.connectionState.collectLatest { state ->
 when (state) {
 is KouziPlatformService.ConnectionState.Error -> {
 _recognitionState.value = RecognitionState.Error(state.message)
 }
 is KouziPlatformService.ConnectionState.Disconnected -> {
 if (_recognitionState.value is RecognitionState.Listening) {
 _recognitionState.value = RecognitionState.Idle
 }
 }
 else -> { /* 其他状态不处理 */ }
 }
 }
 }
 }

 /**
 * 连接到扣子平台
 */
 fun connectToKouziPlatform() {
 kouziPlatformService.connect()
 }

 /**
 * 断开与扣子平台的连接
 */
 fun disconnectFromKouziPlatform() {
 kouziPlatformService.disconnect()
 }

 /**
 * 启动语音识别
 */
 fun startListening() {
 if (_recognitionState.value is RecognitionState.Listening) {
 return
 }

 _recognitionState.value = RecognitionState.Listening

 // 确保已连接到扣子平台
 if (kouziPlatformService.connectionState.value !is KouziPlatformService.ConnectionState.Connected) {
 connectToKouziPlatform()
 }

 // 启动扣子平台的语音识别
 kouziPlatformService.startVoiceRecognition()
 }

 /**
 * 停止语音识别
 */
 fun stopListening() {
 if (_recognitionState.value !is RecognitionState.Listening) {
 return
 }

 _recognitionState.value = RecognitionState.Processing
 kouziPlatformService.stopVoiceRecognition()
 }

 /**
 * 处理识别到的文本
 */
 private fun processRecognizedText(text: String) {
 scope.launch {
 val result = dialogManager.processVoiceInput(text)

 when (result) {
 is VoiceDialogManager.ProcessResult.Command -> {
 // 执行命令
 executeCommand(result.command)
 val responseText = getResponseForCommand(result.command)
 _lastResponseText.value = responseText

 // 使用扣子平台进行语音合成
 kouziPlatformService.speakText(responseText)
 }
 is VoiceDialogManager.ProcessResult.Response -> {
 // 显示响应
 _lastResponseText.value = result.text

 // 使用扣子平台进行语音合成
 kouziPlatformService.speakText(result.text)
 }
 }

 _recognitionState.value = RecognitionState.Idle
 }
 }

 /**
 * 执行机器人命令
 */
 private suspend fun executeCommand(command: RobotCommand) {
 robotRepository.sendCommand(command)
 .onSuccess {
 Log.d(TAG, "命令执行成功: ${command.action}")
 }
 .onFailure { e ->
 Log.e(TAG, "命令执行失败: ${command.action}", e)
 }
 }

 /**
 * 根据命令生成响应文本
 */
 private fun getResponseForCommand(command: RobotCommand): String {
 return when (command.action) {
 CommandAction.START_CLEANING -> "好的，开始清洁"
 CommandAction.STOP_CLEANING -> "已停止清洁"
 CommandAction.RETURN_TO_DOCK -> "正在返回充电座"
 CommandAction.LOCATE -> "已发出提示音，帮助您找到机器人"
 CommandAction.CLEAN_SPOT -> {
 val area = command.params["area"] ?: "指定区域"
 "开始清洁${area}"
 }
 CommandAction.SET_MODE -> {
 val mode = command.params["mode"] ?: "标准"
 "已设置为${mode}模式"
 }
 else -> "命令已执行"
 }
 }

 // 语音识别状态
 sealed class RecognitionState {
 object Idle : RecognitionState()
 object Listening : RecognitionState()
 object Processing : RecognitionState()
 data class Error(val message: String) : RecognitionState()
 }
}
```
</augment_code_snippet>

## 3. 扣子平台连接机器人

现在，我们需要创建一个服务，让扣子平台能够连接到机器人。我们将使用MQTT协议与机器人通信：

<augment_code_snippet path="app/src/main/java/top/minepixel/rdk/robot/RobotMqttService.kt" mode="EDIT">
```kotlin
package top.minepixel.rdk.robot

import android.util.Log
import com.hivemq.client.mqtt.MqttClient
import com.hivemq.client.mqtt.mqtt5.Mqtt5AsyncClient
import com.hivemq.client.mqtt.mqtt5.message.publish.Mqtt5Publish
import com.squareup.moshi.Moshi
import com.squareup.moshi.kotlin.reflect.KotlinJsonAdapterFactory
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.asStateFlow
import kotlinx.coroutines.launch
import top.minepixel.rdk.data.model.CommandAction
import top.minepixel.rdk.data.model.RobotCommand
import top.minepixel.rdk.data.model.RobotStatus
import java.nio.charset.StandardCharsets
import java.util.UUID
import javax.inject.Inject
import javax.inject.Singleton

private const val TAG = "RobotMqttService"

// MQTT主题
private const val TOPIC_STATUS = "devices/robot_status"
private const val TOPIC_COMMAND = "devices/robot_command"
private const val TOPIC_VOICE = "devices/voice_command"

/**
 * 机器人MQTT服务 - 负责与机器人通信
 */
@Singleton
class RobotMqttService @Inject constructor() {
 private val scope = CoroutineScope(Dispatchers.IO)

 // MQTT客户端
 private var mqttClient: Mqtt5AsyncClient? = null

 // JSON解析器
 private val moshi = Moshi.Builder().add(KotlinJsonAdapterFactory()).build()

 // 连接状态
 private val _connectionState = MutableStateFlow<ConnectionState>(ConnectionState.Disconnected)
 val connectionState: StateFlow<ConnectionState> = _connectionState.asStateFlow()

 // 机器人状态
 private val _robotStatus = MutableStateFlow<RobotStatus?>(null)
 val robotStatus: StateFlow<RobotStatus?> = _robotStatus.asStateFlow()

 /**
 * 连接到MQTT服务器
 */
 fun connect(brokerHost: String, brokerPort: Int) {
 if (_connectionState.value is ConnectionState.Connected) {
 return
 }

 _connectionState.value = ConnectionState.Connecting

 try {
 // 创建MQTT客户端
 mqttClient = MqttClient.builder()
 .useMqttVersion5()
 .identifier("rdk-client-" + UUID.randomUUID().toString())
 .serverHost(brokerHost)
 .serverPort(brokerPort)
 .buildAsync()

 // 连接到MQTT服务器
 mqttClient?.connectWith()
 ?.cleanStart(true)
 ?.keepAlive(30)
 ?.send()
 ?.whenComplete { connAck, throwable ->
 if (throwable != null) {
 Log.e(TAG, "连接MQTT服务器失败", throwable)
 _connectionState.value = ConnectionState.Error("连接失败: ${throwable.message}")
 } else {
 Log.d(TAG, "成功连接到MQTT服务器")
 _connectionState.value = ConnectionState.Connected

 // 订阅主题
 subscribeToTopics()
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "创建MQTT客户端失败", e)
 _connectionState.value = ConnectionState.Error("创建客户端失败: ${e.message}")
 }
 }

 /**
 * 断开与MQTT服务器的连接
 */
 fun disconnect() {
 mqttClient?.disconnectWith()
 ?.send()
 ?.whenComplete { _, throwable ->
 if (throwable != null) {
 Log.e(TAG, "断开MQTT连接失败", throwable)
 } else {
 Log.d(TAG, "已断开MQTT连接")
 }

 mqttClient = null
 _connectionState.value = ConnectionState.Disconnected
 }
 }

 /**
 * 订阅MQTT主题
 */
 private fun subscribeToTopics() {
 mqttClient?.subscribeWith()
 ?.topicFilter(TOPIC_STATUS)
 ?.callback { publish -> processStatusMessage(publish) }
 ?.send()
 ?.whenComplete { subAck, throwable ->
 if (throwable != null) {
 Log.e(TAG, "订阅状态主题失败", throwable)
 } else {
 Log.d(TAG, "成功订阅状态主题")
 }
 }
 }

 /**
 * 处理状态消息
 */
 private fun processStatusMessage(publish: Mqtt5Publish) {
 try {
 val payload = String(publish.payloadAsBytes, StandardCharsets.UTF_8)
 Log.d(TAG, "收到状态消息: $payload")

 // 解析JSON
 val adapter = moshi.adapter(RobotStatus::class.java)
 val status = adapter.fromJson(payload)

 if (status != null) {
 _robotStatus.value = status
 }
 } catch (e: Exception) {
 Log.e(TAG, "处理状态消息失败", e)
 }
 }

 /**
 * 发送命令到机器人
 */
 fun sendCommand(command: RobotCommand) {
 if (_connectionState.value !is ConnectionState.Connected) {
 Log.e(TAG, "未连接到MQTT服务器，无法发送命令")
 return
 }

 scope.launch {
 try {
 // 将命令转换为JSON
 val adapter = moshi.adapter(RobotCommand::class.java)
 val json = adapter.toJson(command)

 // 发布命令
 mqttClient?.publishWith()
 ?.topic(TOPIC_COMMAND)
 ?.payload(json.toByteArray(StandardCharsets.UTF_8))
 ?.qos(1)
 ?.send()
 ?.whenComplete { pubAck, throwable ->
 if (throwable != null) {
 Log.e(TAG, "发送命令失败", throwable)
 } else {
 Log.d(TAG, "命令发送成功: ${command.action}")
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "准备命令失败", e)
 }
 }
 }

 /**
 * 发送语音命令到机器人
 */
 fun sendVoiceCommand(text: String) {
 if (_connectionState.value !is ConnectionState.Connected) {
 Log.e(TAG, "未连接到MQTT服务器，无法发送语音命令")
 return
 }

 scope.launch {
 try {
 // 创建语音命令对象
 val voiceCommand = mapOf(
 "type" to "voice",
 "text" to text,
 "timestamp" to System.currentTimeMillis()
 )

 // 转换为JSON
 val adapter = moshi.adapter(Map::class.java)
 val json = adapter.toJson(voiceCommand)

 // 发布语音命令
 mqttClient?.publishWith()
 ?.topic(TOPIC_VOICE)
 ?.payload(json.toByteArray(StandardCharsets.UTF_8))
 ?.qos(1)
 ?.send()
 ?.whenComplete { pubAck, throwable ->
 if (throwable != null) {
 Log.e(TAG, "发送语音命令失败", throwable)
 } else {
 Log.d(TAG, "语音命令发送成功: $text")
 }
 }
 } catch (e: Exception) {
 Log.e(TAG, "准备语音命令失败", e)
 }
 }
 }

 // 连接状态
 sealed class ConnectionState {
 object Disconnected : ConnectionState()
 object Connecting : ConnectionState()
 object Connected : ConnectionState()
 data class Error(val message: String) : ConnectionState()
 }
}
```
</augment_code_snippet>

## 4. 更新RobotRepositoryImpl以使用MQTT服务

