package top.minepixel.rdk.di

import com.hivemq.client.mqtt.MqttClient
import com.hivemq.client.mqtt.mqtt5.Mqtt5AsyncClient
import com.squareup.moshi.Moshi
import com.squareup.moshi.kotlin.reflect.KotlinJsonAdapterFactory
import dagger.Module
import dagger.Provides
import dagger.hilt.InstallIn
import dagger.hilt.android.qualifiers.ApplicationContext
import dagger.hilt.components.SingletonComponent
import android.content.Context
import top.minepixel.rdk.ai.KouziAgentConfig
import javax.inject.Singleton

@Module
@InstallIn(SingletonComponent::class)
object NetworkModule {

 @Provides
 @Singleton
 fun provideMoshi(): Moshi {
 return Moshi.Builder()
 .add(KotlinJsonAdapterFactory())
 .build()
 }

 @Provides
 @Singleton
 fun provideMqttClient(): Mqtt5AsyncClient {
 return MqttClient.builder()
 .useMqttVersion5()
 .identifier("rdk-client-" + System.currentTimeMillis())
 .buildAsync()
 }

 @Provides
 @Singleton
 fun provideKouziAgentConfig(@ApplicationContext context: Context): KouziAgentConfig {
 val config = KouziAgentConfig(context)
 // 在应用启动时配置扣子平台智能体
 // 注意：实际应用中应该从安全的配置源获取API密钥
 config.configure(
 apiKey = "your_kouzi_api_key_here", 
 agentId = "kouzi-agent-robot-assistant"
 )
 return config
 }
}
