import kotlin.coroutines.resumeWithException

private const val TAG = "VoiceInteractionService"

/**
 * 语音交互服务 - 处理语音输入和输出
 */
@Singleton
class VoiceInteractionService @Inject constructor(
 @ApplicationContext private val context: Context,
 private val voiceCommandParser: VoiceCommandParser,
 private val conversationManager: ConversationManager,
 private val robotRepository: RobotRepository
) {
 private val scope = CoroutineScope(Dispatchers.Main)

 // 语音识别器
 private var speechRecognizer: SpeechRecognizer? = null

 // 文本转语音
 private var textToSpeech: TextToSpeech? = null

 // 语音交互状态
 private val _interactionState = MutableStateFlow<InteractionState>(InteractionState.Idle)
 val interactionState: StateFlow<InteractionState> = _interactionState.asStateFlow()

 // 是否初始化完成
 private var isInitialized = false

 /**
 * 初始化语音交互服务
 */
 fun initialize() {
 if (isInitialized) return

 // 初始化语音识别器
 if (SpeechRecognizer.isRecognitionAvailable(context)) {
 speechRecognizer = SpeechRecognizer.createSpeechRecognizer(context)
 } else {
 Log.e(TAG, "语音识别不可用")
 }

 // 初始化文本转语音
 textToSpeech = TextToSpeech(context) { status ->
 if (status == TextToSpeech.SUCCESS) {
 // 设置语言
 val result = textToSpeech?.setLanguage(Locale.CHINA)
 if (result == TextToSpeech.LANG_MISSING_DATA || result == TextToSpeech.LANG_NOT_SUPPORTED) {
 Log.e(TAG, "文本转语音不支持中文")
 } else {
 // 设置语速和音调
 textToSpeech?.setSpeechRate(1.0f)
 textToSpeech?.setPitch(1.0f)

 // 设置进度监听器
 textToSpeech?.setOnUtteranceProgressListener(object : UtteranceProgressListener() {
 override fun onStart(utteranceId: String) {
 _interactionState.value = InteractionState.Speaking
 }

 override fun onDone(utteranceId: String) {
 _interactionState.value = InteractionState.Idle
 }

 override fun onError(utteranceId: String) {
 _interactionState.value = InteractionState.Error("语音合成错误")
 }
 })
 }
 } else {
 Log.e(TAG, "文本转语音初始化失败: $status")
 }
 }

 isInitialized = true
 }

 /**
 * 开始语音识别
 */
 suspend fun startListening(): Result<String> = suspendCancellableCoroutine { continuation ->
 try {
 if (speechRecognizer == null) {
 continuation.resumeWithException(IllegalStateException("语音识别不可用"))
 return@suspendCancellableCoroutine
 }

 _interactionState.value = InteractionState.Listening

 // 设置识别监听器
 speechRecognizer?.setRecognitionListener(object : RecognitionListener {
 override fun onReadyForSpeech(params: Bundle?) {}

 override fun onBeginningOfSpeech() {}

 override fun onRmsChanged(rmsdB: Float) {}

 override fun onBufferReceived(buffer: ByteArray?) {}

 override fun onEndOfSpeech() {}

 override fun onError(error: Int) {
 val errorMessage = when (error) {
 SpeechRecognizer.ERROR_AUDIO -> "音频错误"
 SpeechRecognizer.ERROR_CLIENT -> "客户端错误"
 SpeechRecognizer.ERROR_INSUFFICIENT_PERMISSIONS -> "权限不足"
 SpeechRecognizer.ERROR_NETWORK -> "网络错误"
 SpeechRecognizer.ERROR_NETWORK_TIMEOUT -> "网络超时"
 SpeechRecognizer.ERROR_NO_MATCH -> "没有匹配的结果"
 SpeechRecognizer.ERROR_RECOGNIZER_BUSY -> "识别器忙"
 SpeechRecognizer.ERROR_SERVER -> "服务器错误"
 SpeechRecognizer.ERROR_SPEECH_TIMEOUT -> "语音超时"
 else -> "未知错误: $error"
 }

 _interactionState.value = InteractionState.Error(errorMessage)
 continuation.resumeWithException(RuntimeException(errorMessage))
 }

 override fun onResults(results: Bundle?) {
 val matches = results?.getStringArrayList(SpeechRecognizer.RESULTS_RECOGNITION)
 if (matches != null && matches.isNotEmpty()) {
 val recognizedText = matches[0]
 _interactionState.value = InteractionState.RecognitionResult(recognizedText)
 continuation.resume(recognizedText)
 } else {
 _interactionState.value = InteractionState.Error("未识别到语音")
 continuation.resumeWithException(RuntimeException("未识别到语音"))
 }
 }

 override fun onPartialResults(partialResults: Bundle?) {}

 override fun onEvent(eventType: Int, params: Bundle?) {}
 })

 // 开始识别
 val intent = Intent(RecognizerIntent.ACTION_RECOGNIZE_SPEECH).apply {
 putExtra(RecognizerIntent.EXTRA_LANGUAGE_MODEL, RecognizerIntent.LANGUAGE_MODEL_FREE_FORM)
 putExtra(RecognizerIntent.EXTRA_LANGUAGE, "zh-CN")
 putExtra(RecognizerIntent.EXTRA_MAX_RESULTS, 1)
 putExtra(RecognizerIntent.EXTRA_PARTIAL_RESULTS, true)
 }

 speechRecognizer?.startListening(intent)

 // 取消时停止识别
 continuation.invokeOnCancellation {
 speechRecognizer?.cancel()
 _interactionState.value = InteractionState.Idle
 }
 } catch (e: Exception) {
 Log.e(TAG, "开始语音识别失败", e)
 _interactionState.value = InteractionState.Error("开始语音识别失败: ${e.message}")
 continuation.resumeWithException(e)
 }
 }

 /**
 * 停止语音识别
 */
 fun stopListening() {
 speechRecognizer?.stopListening()
 _interactionState.value = InteractionState.Idle
 }

 /**
 * 语音合成并播放
 */
 fun speak(text: String) {
 if (textToSpeech == null) {
 Log.e(TAG, "文本转语音不可用")
 return
 }

 val utteranceId = UUID.randomUUID().toString()
 textToSpeech?.speak(text, TextToSpeech.QUEUE_FLUSH, null, utteranceId)
 }

 /**
 * 处理语音命令
 */
 suspend fun process
"},{"id":2,"type":2,"content":""},{"id":3,"type":3,"content":"app/src/main/java/top/minepixel/rdk/di/NetworkModule.kt:0-753
