function handleChat(query: string, userInfo: any): any {
 // 简单的闲聊回复库
 const chatResponses = {
 '你好': `你好！我是小清，您的智能清洁助手。${userInfo.schedule ? '您有预约的清洁任务哦。' : '有什么清洁问题需要我帮忙吗？'}`,
 '早上好': '早上好！新的一天开始了，需要我帮您规划今天的清洁工作吗？',
