 57	@Composable
 58	fun MainScreen(viewModel: MainViewModel = hiltViewModel()) {
 59	 val uiState by viewModel.uiState.collectAsState()
 60	 
 61	 Column(
 62	 modifier = Modifier
 63	 .padding(16.dp)
 64	 .safeDrawingPadding(), // 确保内容在安全区域内
