 46	/**
 47	 * 主导航图
 48	 */
 49	@Composable
 50	fun RobotNavGraph(
 51	 navController: NavHostController = rememberNavController(),
 52	 startDestination: String = RobotDestinations.SPLASH_ROUTE,
 53	 modifier: Modifier = Modifier
 54	) {
 55	 // 使用MainScreen作为容器，实现全局底部导航栏
 56	 MainScreen(navController = navController) {
 57	 NavHost(navController = navController, startDestination = startDestination, modifier = modifier) {
 58	 // 启动画面
 59	 composable(RobotDestinations.SPLASH_ROUTE) {
 60	 SplashScreen(
 61	 onSplashFinished = {
 62	 navController.navigate(RobotDestinations.LOGIN_ROUTE) {
 63	 popUpTo(RobotDestinations.SPLASH_ROUTE) { inclusive = true }
 64	 }
 65	 }
 66	 )
 67	 }
