 120	 
 121	 override suspend fun updateTaskStatus(taskId: String, status: TaskStatus): Result<Boolean> {
 122	 return try {
 123	 // 更新本地状态
 124	 val currentTasks = _cleaningTasks.value.toMutableList()
 125	 val taskIndex = currentTasks.indexOfFirst { it.id == taskId }
 126	 
 127	 if (taskIndex != -1) {
 128	 val task = currentTasks[taskIndex]
 129	 currentTasks[taskIndex] = task.copy(status = status)
 130	 _cleaningTasks.value = currentTasks
 131	 }
 132	 
 133	 // 模拟成功
 134	 Result.success(true)
 135	 } catch (e: Exception) {
 136	 Result.failure(e)
 137	 }
 138	 }
 139	 
 140	 override suspend fun sendCommand(command: RobotCommand): Result<Boolean> {
 141	 return try {
 142	 // 实际中这里应该发送MQTT消息到机器人
 143	 // mqttClient.publish(MQTT_TOPIC_COMMAND, command.toJson(), MQTT_QOS, false)
 144	 
 145	 // 更新本地状态以模拟命令效果
 146	 when (command.action) {
 147	 CommandAction.START_CLEANING -> {
 148	 val current = _robotStatus.value
 149	 _robotStatus.value = current.copy(
 150	 mode = RobotMode.CLEANING,
 151	 cleaningProgress = 0
 152	 )
 153	 }
 154	 CommandAction.STOP_CLEANING -> {
 155	 val current = _robotStatus.value
 156	 _robotStatus.value = current.copy(
 157	 mode = RobotMode.IDLE,
 158	 cleaningProgress = 0
 159	 )
 160	 }
 161	 CommandAction.PAUSE_CLEANING -> {
 162	 val current = _robotStatus.value
 163	 if (current.mode == RobotMode.CLEANING) {
 164	 _robotStatus.value = current.copy(
 165	 mode = RobotMode.IDLE
 166	 )
 167	 }
 168	 }
 169	 CommandAction.RESUME_CLEANING -> {
 170	 val current = _robotStatus.value
 171	 if (current.mode == RobotMode.IDLE) {
 172	 _robotStatus.value = current.copy(
 173	 mode = RobotMode.CLEANING
 174	 )
 175	 }
 176	 }
 177	 CommandAction.RETURN_TO_DOCK -> {
 178	 val current = _robotStatus.value
 179	 _robotStatus.value = current.copy(
 180	 mode = RobotMode.RETURNING_TO_DOCK
 181	 )
 182	 }
 183	 else -> {
 184	 // 其他命令不做模拟
 185	 }
 186	 }
 187	 
 188	 // 模拟成功
 189	 Result.success(true)
 190	 } catch (e: Exception) {
 191	 Result.failure(e)
 192	 }
 193	 }
