 18	private const val TAG = "HomeViewModel"
 19	
 20	@HiltViewModel
 21	class HomeViewModel @Inject constructor(
 22	 private val robotRepository: RobotRepository
 23	) : ViewModel() {
 24	 
 25	 // 机器人状态
 26	 val robotStatus: StateFlow<RobotStatus> = robotRepository
 27	 .getRobotStatus()
 28	 .stateIn(
 29	 scope = viewModelScope,
 30	 started = SharingStarted.WhileSubscribed(5000),
 31	 initialValue = RobotStatus(
 32	 id = "",
 33	 name = "智能清洁机器人",
 34	 isOnline = false,
 35	 battery = 0
 36	 )
 37	 )
 38	 
