 34	 id = "robot_001",
 35	 name = "小米扫地机器人",
 36	 type = DeviceType.VACUUM_ROBOT,
 37	 isOnline = true,
 38	 isConnected = true,
 39	 batteryLevel = 85,
 40	 lastActivity = "2分钟前"
 41	 )
 42	 )
