 1	import socket,struct,fcntl
 2	from time import sleep
 3	import serial
 4	
 5	def get_ip_address(ifname):
 6	 hostname = socket.socket(socket.AF_INET,socket.SOCK_DGRAM)
 7	 return socket.inet_ntoa(fcntl.ioctl(hostname.fileno(),0x8915,struct.pack('256s',bytes(ifname[:15],'utf-8')))[20:24])
 8	
 9	def split_ip(ip):
 10	 parts = ip.split('.')
 11	 return parts
 12	
 13	def main():
 14	 ser = serial.Serial("/dev/ttyUSB0", 115200,timeout=.2)
 15	 ip = get_ip_address('wlan0')
 16	 part,part1,part2,part3 = split_ip(ip)
 17	
 18	
 19	
 20	 #part_H = (int(part) >> 8) & 0xFF
 21	 part_L = int(part) & 0xFF
 22	
 23	 #part1_H = (int(part1) >> 8) & 0xFF
 24	 part1_L = int(part1) & 0xFF
 25	
 26	 #part2_H = (int(part2) >> 8) & 0xFF
 27	 part2_L = int(part2) & 0xFF
 28	
 29	 #part3_H = (int(part3) >> 8) & 0xFF
 30	 part3_L = int(part3) & 0xFF
 31	
 32	 cmd = [0xFF,0xFC,0x07,0x71, part_L, part1_L,part2_L,part3_L]
 33	 checksum = sum(cmd,5) & 0xFF
 34	 cmd.append(checksum)
 35	 ser.write(cmd)
 36	 sleep(0.001)
 37	
 38	
 39	if __name__ == "__main__":
 40	 main()
