 34	@Singleton
 35	class RobotRepositoryImpl @Inject constructor(
 36	 @ApplicationContext private val context: Context,
 37	 @Named("IoDispatcher") private val ioDispatcher: CoroutineDispatcher
 38	) : RobotRepository {
 39	
 40	 private var mqttClient: Mqtt5AsyncClient? = null
 41	 private val moshi = Moshi.Builder().add(KotlinJsonAdapterFactory()).build()
 42	 
 43	 // 状态流
 44	 private val _robotStatus = MutableStateFlow(
 45	 RobotStatus(
 46	 id = "demo_robot_1",
 47	 name = "智能清洁机器人",
 48	 isOnline = false,
 49	 battery = 80,
 50	 mode = RobotMode.IDLE
 51	 )
 52	 )
 53	 
 54	 private val _detectedItems = MutableStateFlow<List<DetectedItem>>(emptyList())
 55	 private val _cleaningTasks = MutableStateFlow<List<CleaningTask>>(emptyList())
 56	 
