 81	 
 82	 override fun getDetectedItems(): Flow<List<DetectedItem>> = _detectedItems.asStateFlow()
 83	 
 84	 override fun getCleaningTasks(): Flow<List<CleaningTask>> = _cleaningTasks.asStateFlow()
