 1	package top.minepixel.rdk.data.model
 2	
 3	import com.squareup.moshi.Json
 4	import com.squareup.moshi.JsonClass
 5	
 6	/**
 7	 * 机器人状态信息
 8	 */
 9	@JsonClass(generateAdapter = true)
 10	data class RobotStatus(
 11	 val id: String,
 12	 val name: String,
 13	 val isOnline: Boolean = false,
 14	 val battery: Int = 0,
 15	 val mode: RobotMode = RobotMode.IDLE,
 16	 val position: RobotPosition? = null,
 17	 val environment: EnvironmentInfo? = null,
 18	 val cleaningProgress: Int = 0,
 19	 val errorCode: Int? = null,
 20	 val lastUpdated: Long = System.currentTimeMillis()
 21	)
 22	
 23	/**
 24	 * 机器人位置信息
 25	 */
 26	@JsonClass(generateAdapter = true)
 27	data class RobotPosition(
 28	 val x: Double,
 29	 val y: Double,
 30	 val orientation: Double, // 方向角度，0-360度
 31	 val roomId: String? = null
 32	)
 33	
 34	/**
 35	 * 环境信息（温湿度等）
 36	 */
 37	@JsonClass(generateAdapter = true)
 38	data class EnvironmentInfo(
 39	 val temperature: Float, // 摄氏度
 40	 val humidity: Float, // 百分比
 41	 val dustLevel: Int = 0 // 灰尘等级 0-100
 42	)
 43	
 44	/**
 45	 * 识别到的物品信息
 46	 */
 47	@JsonClass(generateAdapter = true)
 48	data class DetectedItem(
 49	 val id: String,
 50	 val type: ItemType,
 51	 val confidence: Float, // 0-1
 52	 val position: RobotPosition,
 53	 val timestamp: Long = System.currentTimeMillis()
 54	)
 55	
 56	/**
 57	 * 清洁任务定义
 58	 */
 59	@JsonClass(generateAdapter = true)
 60	data class CleaningTask(
 61	 val id: String,
 62	 val name: String,
 63	 val rooms: List<String>, // 房间ID列表
 64	 val mode: CleaningMode,
 65	 val startTime: Long? = null, // 可以是定时任务
 66	 val status: TaskStatus = TaskStatus.PENDING,
 67	 val avoidItems: Boolean = true // 是否避开检测到的贵重物品
 68	)
