 40	 val detectedItems: StateFlow<List<DetectedItem>> = robotRepository
 41	 .getDetectedItems()
 42	 .stateIn(
 43	 scope = viewModelScope,
 44	 started = SharingStarted.WhileSubscribed(5000),
 45	 initialValue = emptyList()
 46	 )
 47	 
 48	 init {
 49	 // 如果可能，自动连接到机器人
 50	 viewModelScope.launch {
 51	 connectToRobot("demo_robot_1")
 52	 }
 53	 }
 54	 
 55	 /**
 56	 * 发送命令到机器人
 57	 */
 58	 fun sendCommand(command: RobotCommand) {
 59	 viewModelScope.launch {
 60	 robotRepository.sendCommand(command)
 61	 .onSuccess {
 62	 Log.d(TAG, "命令发送成功: ${command.action}")
 63	 }
 64	 .onFailure { e ->
 65	 Log.e(TAG, "命令发送失败: ${command.action}", e)
 66	 }
 67	 }
 68	 }
 69	 
 70	 /**
 71	 * 连接到机器人
 72	 */
 73	 fun connectToRobot(robotId: String) {
 74	 viewModelScope.launch {
 75	 robotRepository.connectToRobot(robotId)
 76	 .onSuccess {
 77	 Log.d(TAG, "连接到机器人成功: $robotId")
 78	 }
 79	 .onFailure { e ->
 80	 Log.e(TAG, "连接到机器人失败: $robotId", e)
 81	 }
 82	 }
 83	 }
 84	 
 85	 /**
 86	 * 断开与机器人的连接
 87	 */
 88	 fun disconnectFromRobot() {
 89	 viewModelScope.launch {
 90	 robotRepository.disconnectFromRobot()
 91	 .onSuccess {
 92	 Log.d(TAG, "断开与机器人的连接成功")
 93	 }
 94	 .onFailure { e ->
 95	 Log.e(TAG, "断开与机器人的连接失败", e)
 96	 }
 97	 }
 98	 }
