 74	 popUpTo(RobotDestinations.LOGIN_ROUTE) { inclusive = true }
 75	 }
 76	 },
 77	 onNavigateToRegister = {
 78	 navController.navigate(RobotDestinations.REGISTER_ROUTE)
 79	 },
 80	 onNavigateToForgotPassword = {
 81	 navController.navigate(RobotDestinations.FORGOT_PASSWORD_ROUTE)
 82	 }
 83	 )
 84	 }
 85	 
 86	 composable(RobotDestinations.REGISTER_ROUTE) {
 87	 RegisterScreen(
 88	 onRegisterSuccess = {
 89	 navController.navigate(RobotDestinations.HOME_ROUTE) {
 90	 popUpTo(RobotDestinations.LOGIN_ROUTE) { inclusive = true }
 91	 }
 92	 },
 93	 onNavigateBack = {
 94	 navController.popBackStack()
 95	 }
 96	 )
