 75	 robotRepository.connectToRobot(robotId)
 76	 .onSuccess {
 77	 Log.d(TAG, "连接到机器人成功: $robotId")
 78	 }
 79	 .onFailure { e ->
 80	 Log.e(TAG, "连接到机器人失败: $robotId", e)
 81	 }
 82	 }
 83	 }
