 10	
 11	@Singleton
 12	class DeviceManager @Inject constructor() {
 13	 
 14	 // 设备列表状态
 15	 private val _devices = MutableStateFlow(getInitialDevices())
 16	 val devices: StateFlow<List<SmartDevice>> = _devices.asStateFlow()
 17	 
 18	 // 设备统计
 19	 val totalDevices: StateFlow<Int> = MutableStateFlow(1)
 20	 val onlineDevices: StateFlow<Int> = MutableStateFlow(1)
 21	 val connectedDevices: StateFlow<Int> = MutableStateFlow(1)
 22	 
 23	 init {
 24	 // 初始化设备统计
 25	 updateDeviceStats()
 26	 }
 27	 
 28	 /**
 29	 * 获取初始设备列表
 30	 */
 31	 private fun getInitialDevices(): List<SmartDevice> {
 32	 return listOf(
 33	 SmartDevice(
 34	 id = "robot_001",
 35	 name = "小米扫地机器人",
 36	 type = DeviceType.VACUUM_ROBOT,
 37	 isOnline = true,
 38	 isConnected = true,
 39	 batteryLevel = 85,
 40	 lastActivity = "2分钟前"
 41	 )
 42	 )
 43	 }
...
 77	 
 78	 /**
 79	 * 切换设备连接状态
 80	 */
 81	 fun toggleDeviceConnection(deviceId: String) {
 82	 updateDevice(deviceId) { device ->
 83	 device.copy(isConnected = !device.isConnected)
 84	 }
 85	 }
 86	 
 87	 /**
 88	 * 切换设备在线状态
 89	 */
 90	 fun toggleDeviceOnline(deviceId: String) {
 91	 updateDevice(deviceId) { device ->
 92	 device.copy(isOnline = !device.isOnline)
 93	 }
 94	 }
 95	 
 96	 /**
 97	 * 更新设备统计信息
 98	 */
 99	 private fun updateDeviceStats() {
 100	 val currentDevices = _devices.value
 101	 (totalDevices as MutableStateFlow).value = currentDevices.size
 102	 (onlineDevices as MutableStateFlow).value = currentDevices.count { it.isOnline }
 103	 (connectedDevices as MutableStateFlow).value = currentDevices.count { it.isConnected }
 104	 }
 105	 
 106	 /**
 107	 * 获取设备数量
 108	 */
 109	 fun getTotalDevicesCount(): Int = _devices.value.size
 110	 
 111	 /**
